// Draw lines
draw(p--s--q--p--r--q);
// Label points
label("$R$", r, SW);
label("$Q$", q, SE);
label("$S$", s, N);
label("$P$", p, NW);
// Label angles
label("$y^\circ$", q, 4 * W + 2 * NW);
label("$y^\circ$", q, N + 5 * NW);
label("$y^\circ$", r, 2 * NE + 3 * E);
label("$2y^\circ$", s, 3 * S + SW);
