dot(dots);
draw(A--B--C--cycle);
draw(D--B);
label("$A$",A,SW);
label("$B$",B,SE);
label("$C$",C,NE);
label("$D$",D,NW);
label("$6\sqrt{2}$",(A+B)/2,S);
